<?php/** * Taxonomy Post Format template * * @package ProperWeb * @subpackage ProperFramework-BS * @since ProperFramework-BS 1.3 */?><?php get_header(); ?><div class="row row-with-sidebar">    <div class="cell">        <div class="article article-with-sidebar box full-width">        <h2 class="title"><?php _e('Galleries','properweb'); ?></h2><?php if(have_posts()): while(have_posts()): the_post(); ?>        <?php             if ( has_post_thumbnail() ) {                the_post_thumbnail( 'thumbnail', array( 'class' => 'thumbnail alignleft') );            }        ?>        <h2 class="page-header"><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>        <?php the_excerpt(); ?>        <p class="text-center"><a class="read-more btn btn-primary btn-md round" href="<?php echo get_permalink( get_the_ID() ) ?>"><?php _e('Go to gallery','properweb'); ?></a></p><?php endwhile;     /* Previous/next page navigation */    pweb_posts_pagination( array(            'mid_size'      => 2,            'prev_text'     => __( '<span class="glyphicon glyphicon-chevron-left"></span>' ),            'next_text'     => __( '<span class="glyphicon glyphicon-chevron-right"></span>' ),    ) );        else : ?>	<div class="article article-with-sidebar box full-width er404">            <?php include_once (get_template_directory().'/parts/no-post.php'); ?>	</div><?php endif; ?>        </div>    </div>    <div id="aside" class="right-sidebar">        <div  class="box with-pads">            <ul id="aside-sidebar" class="sidebar">                <?php get_sidebar('default'); ?>                     <?php dynamic_sidebar( 'Post Aside' ); ?>            </ul>        </div>    </div></div><?php get_footer(); ?>